import React, { Component } from 'react';
import TextLoop from "react-text-loop";
import Particles from 'react-particles-js';

import * as Styled from './landingpage.style';
// need to fix height issue

class LandingPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            open:true
        }
    }
    componentDidMount() {
        if(this.state.open){
            window.scrollTo(0, 0);
            document.body.style.overflow = 'hidden';
        }
    }

    render() {
        return (
            <Styled.Wrapper>
                <Styled.Heading>
                    <div className="landingBlock">
                <span className="helloTitle">
                  Hello World.
                </span>
                        <br/>
                        My name is
                        James Pan.&nbsp;
                        <br/>
                        I'm<span> </span>
                        <TextLoop
                            interval={2500}
                            springConfig={{ stiffness: 30, damping: 10 }}
                            mask
                        >
                <span className="scrollTitle">
                  a Student
                </span>
                            <span className="scrollTitle">
                  a Web Developer
                </span>
                            <span className="scrollTitle">
                  an Engineer
                </span>
                            <span className="scrollTitle">
                  a Swimmer
                </span>
                            <span className="scrollTitle">
                  a Tech Geek
                </span>
                            <span className="scrollTitle">
                  a Snowboarder
                </span>
                            <span className="scrollTitle">
                  an Archer
                </span>
                            <span className="scrollTitle">
                  a Game Enthusiast
                </span>
                            <span className="scrollTitle">
                  a Designer
                </span>
                            <span className="scrollTitle">
                  a Sci-Fi Fan
                </span>
                            <span className="scrollTitle">
                  a Melophile
                </span>
                        </TextLoop>
                        <span> </span>
                        from<span> </span>
                        <span>
                <a
                    href="https://www.ubc.ca/"
                    target="_blank"
                    rel="noopener noreferrer"
                    className="city-link"
                    aria-label="UBC Website"
                >
                  <span
                      tabIndex="-1"
                      className="city-link_content"
                  >
                    UBC
                  </span>
                </a>
              </span>
                        .
                    </div>
                </Styled.Heading>
                <Particles
                    params={{
                        particles: {
                            number: {
                                value: 90,
                            },
                            size: {
                                value: 2
                            }
                        },
                        interactivity: {
                            detect_on: "canvas",
                            events: {
                                onhover: {
                                    enable: true,
                                    mode: "grab"
                                },
                                onclick: {
                                    enable: true,
                                    mode: "push"
                                },
                                resize: true
                            },
                        },
                        "retina_detect": true
                    }}
                />
            </Styled.Wrapper>
        );
    }
}

export default LandingPage;
