import React, { Component } from 'react';
import TextLoop from "react-text-loop";
import Particles from 'react-particles-js';

import * as Styled from './LandingPage.style';

class Landing extends Component {
	constructor(props) {
		super(props);
		this.state = {
			open:true
		}
	}
	componentDidMount() {
		if(this.state.open){
			window.scrollTo(0, 0);
			document.body.style.overflow = 'hidden';
		}
	}

	render() {
		return (
			<Styled.Wrapper>
				<Styled.Heading>
					<div className="landingBlock">
				<span className="helloTitle">
				  Welcome, <span> </span>
				</span>
						<TextLoop
							interval={2500}
							springConfig={{ stiffness: 30, damping: 10 }}
							mask
						>
				<span className="scrollTitle">
				  Guardian.
				</span>
	<span className="scrollTitle">
				  Lightbearer.
				</span>
						</TextLoop>
					</div>
				</Styled.Heading>
				<Particles
					params={{
						particles: {
							number: {
								value: 90,
							},
							size: {
								value: 2
							}
						},
						interactivity: {
							detect_on: "canvas",
							events: {
								onhover: {
									enable: true,
									mode: "grab"
								},
								onclick: {
									enable: true,
									mode: "push"
								},
								resize: true
							},
						},
						"retina_detect": true
					}}
				/>
			</Styled.Wrapper>
		);
	}
}

export default Landing;
